mapper.select:
SELECT
	*
	FROM USERS
	WHERE ur_id = #{urId}
	AND ur_pw = #{urPw}

SELECT
	COUNT(*)
	FROM USERS
	WHERE ur_id = #{urId}
	AND ur_pw = #{urPw}

mapper.update:
UPDATE
	USERS SET
		ur_phone = #{urPhone}
		,ur_email = #{urEmail}
		,ur_addr = #{urAddr}
		WHERE UR_NO= #{urNo}

